import React from 'react'
import {Row} from 'react-bootstrap'
import {HistorietaAudio} from './HistorietaAudio'

export const Historieta = ({params}) => {

    const handleAudio = (e) => {
        console.log(e);
    }

    return (
        <Row>
            {
                params.Vinetas.map(vineta => (
                    <div key={vineta.id} className='historieta'>
                        {   
                            (vineta.Audios !== null)
                            && <HistorietaAudio params={vineta.Audios} handleAudio={handleAudio} />
                        }
                        <img src={vineta.Imagen.url} alt={vineta.Titulo}/>
                    </div>
                ))
            }
        </Row>
    )
}
