import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { Board } from './content/Board'

export const Content = ({drawerWidth, params}) => {

    const useStyles = makeStyles((theme) => ({

        // necessary for content to be below app bar
        toolbar: theme.mixins.toolbar,
        drawerPaper: {
          width: drawerWidth
        },

        content: {
          flexGrow: 1,
          padding: theme.spacing(3),
        },
    }));
    const classes = useStyles();
    const content = params.locals.menus.find(menu => menu.name === params.layout);

    const Icon = ({params}) => {
      const TheIcon = params
      return <TheIcon />
    }

    const Components = content.component;

    return (
        <main className={classes.content}>
            <div className={classes.toolbar} />
            {
              (params.layout === 'board' || params.layout === undefined)
              &&<Board params={params}/>
            }
            {
              (content.name !== 'board') && <Icon params={content.component} />
            }
        </main>
    )
}
