import React from 'react'
import { Accordion, Card, Row, Col } from "react-bootstrap";

export const Acordeon = ({params}) => {

    console.log(params.Tabs);

    return (
        <Accordion>
            {
                params.Tabs.map(tab => (
                    <div>
                        <Accordion.Toggle as={Card.Header} eventKey={tab.id}>
                            {tab.Titulo}
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey={tabid}>
                            <Row>
                                <Col>
                                    <div className="intro" dangerouslySetInnerHTML={{ __html: tab.Contenido }} />
                                </Col>
                                <Col>
                                    <span><img src={tab.Imagen[0].url} alt={tab.Titulo}/></span>
                                </Col>
                            </Row>
                        </Accordion.Collapse>
                    </div>
                ))
            }
        </Accordion>
    )
}
