import React, {useRef, useState} from 'react'
import {Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faStopCircle } from '@fortawesome/free-solid-svg-icons'

export const Audio = ({url, handleAudio, id, idPlay}) => {

    const [valPlay, setValPlay] = useState(true);
    const [icon, setIcon] = useState(faVolumeUp);
    const sondRef = useRef();

    const actionAudio = () => {
       if(valPlay === true){
        setIcon(faStopCircle);
        sondRef.current.play();
        handleAudio(id);
       }else{
        setIcon(faVolumeUp);
        sondRef.current.pause();
       }
        setValPlay(!valPlay);
    }

    return (
        <div className="btoAudio">
            <Button variant="primary" onClick={() =>  actionAudio() }>
                <FontAwesomeIcon icon={icon}/> Escucha con atenci√≥n
                <audio src={url} ref={sondRef} /> 
            </Button>
        </div>                
    )
}
