import React, {useState } from 'react'
import {Row} from 'react-bootstrap'
import {Audio} from './Audio'

export const Historieta = ({params}) => {

    const [valAudioPlay, setAudioPlay] = useState(0);
    const handleAudio = (id, setValPlay, setIcon, sondRef, valPlay) => {
        setAudioPlay(id);
    }

    return (
        <Row>
            {
                params.Vinetas.map(vineta => (
                    <div key={vineta.id} className='historieta'>
                        {   
                            (vineta.Audios !== null)
                            && <Audio url={vineta.Audios.url} handleAudio={handleAudio} id={vineta.id} idPlay={valAudioPlay}/>
                        }
                        <img src={vineta.Imagen.url} alt={vineta.Titulo}/>
                    </div>
                ))
            }
        </Row>
    )
}
