import React from 'react'
import { Accordion, Card, Row, Col, Container } from "react-bootstrap";

export const Acordeon = ({params}) => {

    console.log(params.Tabs);

    return (
        <Accordion>
            {
                params.Tabs.map(tab => (
                    <div key={tab.id} className='acordeon'>
                        <Accordion.Toggle as={Card.Header} eventKey={tab.id}>
                            {tab.Titulo}
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey={tab.id}>
                            <Container>
                                <Row className="justify-content-center">
                                    <Col className="content">
                                        <div dangerouslySetInnerHTML={{ __html: tab.Contenido }} />
                                    </Col>
                                    <Col className="justify-content-center">
                                        <span><img src={tab.Imagen.url} alt={tab.Titulo}/></span>
                                    </Col>
                                </Row>
                            </Container>
                        </Accordion.Collapse>
                    </div>
                ))
            }
        </Accordion>
    )
}
