import React, {useRef, useState} from 'react'
import {Row, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faStopCircle } from '@fortawesome/free-solid-svg-icons'
import {Historieta_audio} from './Historieta_audio'

export const Historieta = ({params}) => {
    return (
        <Row>
            {
                params.Vinetas.map(vineta => (
                    <div key={vineta.id} className='historieta'>
                        {   
                            (vineta.Audio !== null)
                            && <Historieta_audio params={vineta.Audio.url} />
                        }
                        <img src={vineta.Imagen.url} alt={vineta.Titulo}/>
                    </div>
                ))
            }
        </Row>
    )
}
