import React from 'react'
import {Row, Col, Container } from 'react-bootstrap'
import {Tematicas} from '../data/tematicas'
import {Link, Router} from 'react-router-dom'

export const Dps = () => {

    //console.log(Tematicas);

    return (
        <Row>
            <Col id="main" xs={{span:10, offset: 1}} >
            <Container>
                <Row>
                    <Col sm={12} md={7} className="btoTematicas">
                        <Row className="justify-content-center">
                            {Tematicas.map(tematica => (
                                <Col sm={12} md={8} className="bto" key={tematica.Url}>
                                    <Router>
                                        <Link to={location => ({ ...location, pathname: "/tematicas/"+tematica.Url })} replace>
                                            <Row>
                                                <Col xs={2} className="ico">
                                                    <img src={tematica.Icon.url} alt={tematica.Tematica} />
                                                </Col>
                                                <Col className="title" >{tematica.Tematica}</Col>
                                            </Row>
                                        </Link>
                                    </Router>
                                </Col>
                            ))}
                        </Row>
                    </Col>
                    <Col sm={5} className="bannerTematicas d-none d-md-block d-xxl-block">
                        Banner
                    </Col>
                </Row>
            </Container>
            </Col>
        </Row>
    )
}


