import React from 'react';
import PropTypes from 'prop-types';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import logo from '../../assets/img/logo_compuesto.png'
import MailIcon from '@material-ui/icons/Mail';
import SettingsIcon from '@material-ui/icons/Settings';
import { useHistory } from "react-router-dom";

function Menu(props) {
    let history = useHistory();
    const useStyles = makeStyles((theme) => ({
        drawer: {
          [theme.breakpoints.up('sm')]: {
            width: props.drawerWidth,
            flexShrink: 0,
          },
        },
        // necessary for content to be below app bar
        drawerPaper: {
          width: props.drawerWidth
        },
    }));
    const { window } = props;
    const classes = useStyles();
    const theme = useTheme();

    const clicItem = () => { history.push("/admin/board"); }

    const drawer = (
        <div className="Menu">
            <img id="logo" src={logo} alt="idiky Logo"  />
            <Divider />
              <ListItem button onClick={clicItem}>
                  <ListItemIcon><MailIcon /></ListItemIcon>
                  <ListItemText primary='Inicio' />
              </ListItem>
            <Divider />
            <List>
                {props.params.locals.menus.m2.map((info) => (
                <ListItem button key={info.key}>
                    <ListItemIcon>
                      {
                        ()=>{ 
                          const SpecificStory = info.icon;
                          console.log("----- "+SpecificStory);
                          return <SpecificStory  />;
                        }
                      }
                    </ListItemIcon>
                    <ListItemText primary={info.label} />
                </ListItem>
                ))}
            </List>
            <Divider />
            <List>
                {props.params.locals.menus.m3.map((info) => (
                  <ListItem button key={info.key}>
                    <ListItemIcon>
                      <InboxIcon/>
                    </ListItemIcon>
                    <ListItemText primary={info.label} />
                  </ListItem>
                ))}
            </List>
    </div>
    );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={props.mobileOpen}
            onClose={props.handleDrawerToggle.bind(this)}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
    </nav>
  );
}

Menu.propTypes = {
  window: PropTypes.func,
};

export default Menu;
