import React from 'react'
import {Row, Col, Container } from 'react-bootstrap'

export const IntroTematica = ({params}) => {
    console.log(params.Imagen.length);
    const img = (params.Imagen.length > 0)? params.Imagen[0].url : 'https://res.cloudinary.com/dtiqybyoh/image/upload/v1626955885/ganerico_42055a70c7.png';
    const audio = (params.Audios.length > 0)? params.Audios[0].url : '';
    const actionAudio = (ref) => {
        if(ref == 1){
            if(valPlay1 === true){
                setIcon1(faStopCircle);
                sondRef1.current.play();
                setIcon2(faVolumeUp);
                sondRef2.current.pause();
                setValPlay1(!valPlay1);
            }else{
                setIcon1(faVolumeUp);
                sondRef1.current.pause();
            }
            setValPlay1(!valPlay1);
        }else{
            if(valPlay2 === true){
                setIcon2(faStopCircle);
                sondRef2.current.play();
                setIcon1(faVolumeUp);
                sondRef1.current.pause();
                setValPlay1(!valPlay1);
            }else{
                setIcon2(faVolumeUp);
                sondRef2.current.pause();
            }
            setValPlay2(!valPlay2);
        }  
    }
    return (
        <Container className="content">
            <Row>
                <Col >
                    <h1>{params.Titulo}</h1>
                    <hr/>
                    <div className="intro" dangerouslySetInnerHTML={{ __html: params.Introduccion }} />
                    <audio src={audio1} ref={sondRef1} />
                </Col>
                <Col className="imgIntro">
                    <span><img src={img} /></span>
                </Col>
            </Row>
        </Container>
    )
}



