import React, {useState, useEffect, Fragment, lazy, Suspense} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { Board } from './content/Board'

function Loading() {
  return <div>Loading</div>;
}

export const Content = ({drawerWidth, params}) => {

   let Modules = (<div>Loading</div>);
    const useStyles = makeStyles((theme) => ({

        // necessary for content to be below app bar
        toolbar: theme.mixins.toolbar,
        drawerPaper: {
          width: drawerWidth
        },

        content: {
          flexGrow: 1,
          padding: theme.spacing(3),
        },
    }));
    const classes = useStyles();

    const [module, setModule] = useState(Loading);

   useEffect(() => {
      const load = async () => {
        //const Modules = (await import("./content/Store")).default;
        Modules = lazy(() => import('./content/Store'));
        //setModule(<Modules params = {params}/>);
        setModule("hola");
      };
      load();
   }, [params.layout]);

    return (
        <main className={classes.content}>
            <div className={classes.toolbar} />
            {
              (params.layout === 'board' || params.layout === undefined)
              &&<Board params={params}/>
            }
            <Suspense fallback={<div>Page is Loading...</div>}>
        <section>
            <Modules/>
            </section>
            </Suspense>
        </main>
    )
}
