import React from 'react'
import { Card, Row, Col, Container } from "react-bootstrap";

export const TarjetaGiro = ({params}) => {

    return (
        <Row>
            {   
                params.Tarjeta.map(tarjeta => (
                    <Col key={tarjeta.id} className="tarjeta" xs={12} sm={12} lg={6}>
                        <Card  className="card-flip h-100">
                            <div className="card-front text-white bg-dark">
                                <div className={tarjeta.ColorA+" card-body"} >
                                {
                                    (tarjeta.ImagenA !== null)
                                    &&<img src={tarjeta.ImagenA.url} style={{width:"100%"}} alt="" />
                                }
                                {
                                    (tarjeta.ImagenA === null)
                                    &&<Container className="contentA"><div dangerouslySetInnerHTML={{ __html: tarjeta.CaraA }} /></Container> 
                                }
                                </div>
                            </div>
                            <div className="card-back bg-white">
                                <div className={tarjeta.ColorB+" card-body cardRevers"}>
                                {
                                    (tarjeta.ImagenB !== null)
                                    && <img src={tarjeta.ImagenB.url} style={{width:"100%"}} alt="" />
                                }
                                {
                                    (tarjeta.ImagenB === null)
                                    && <Container className="contentB"><div dangerouslySetInnerHTML={{ __html: tarjeta.CaraB }} /></Container>
                                }
                                </div>
                            </div>
                        </Card>
                    </Col>
                ))
            }
        </Row>
    )
}


