import React from 'react'
import { Accordion, Card, Row, Col, Container } from "react-bootstrap";

export const Acordeon = ({params}) => {

    return (
        <Accordion>
            {
                params.Tabs.map(tab => (
                    <div key={tab.id} className='acordeon'>
                        <Accordion.Toggle as={Card.Header} eventKey={tab.id}>
                            {tab.Titulo}
                        </Accordion.Toggle>
                        <Accordion.Collapse eventKey={tab.id} className={tab.Color}>
                            <Container>
                                <Row>
                                    <Col xs={12} sm={12} lg={6} className="content">
                                        <div dangerouslySetInnerHTML={{ __html: tab.Contenido }} />
                                    </Col>
                                    <Col xs={12} sm={12} lg={6} >
                                        <img src={tab.Imagen.url} alt={tab.Titulo}/>
                                    </Col>
                                </Row>
                            </Container>
                        </Accordion.Collapse>
                    </div>
                ))
            }
        </Accordion>
    )
}
