import React, {useRef, useState} from 'react'
import {Row, Col, Container, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faStopCircle } from '@fortawesome/free-solid-svg-icons'

export const IntroTematica = ({params}) => {
    const img = (params.Imagen.length > 0)? params.Imagen[0].url : 'https://res.cloudinary.com/dtiqybyoh/image/upload/v1626955885/ganerico_42055a70c7.png';

    const [valPlay, setValPlay] = useState(true);
    const [icon, setIcon] = useState(faVolumeUp);
    const sondRef = useRef();

    const actionAudio = () => {
       if(valPlay === true){
        setIcon(faStopCircle);
        sondRef.current.play();

       }else{
        setIcon(faVolumeUp);
        sondRef.current.pause();
       }
        setValPlay(!valPlay);
    }

    return (
        <Container className="content">
            <Row>
                <Col>
                    <h1>{params.Titulo}</h1>
                    <hr/>
                    <div className="intro" dangerouslySetInnerHTML={{ __html: params.Introduccion }} />
                    {   (params.Audios !== null)
                        && <Button variant="primary" onClick={() =>  actionAudio() }>
                            <FontAwesomeIcon icon={icon}/> Escucha con atención
                            <audio src={params.Audios.url} ref={sondRef} />
                           </Button>
                    }
                </Col>
                <Col className="imgIntro">
                    <span><img src={img} alt={params.Titulo}/></span>
                </Col>
            </Row>
        </Container>
    )
}



