import React from 'react'
import {Row, Col } from 'react-bootstrap'
import {Tematicas} from '../data/tematicas'
import {Link} from 'react-router-dom'

export const Dps = () => {
    return (
        <Row>
            <Col sm={12} md={6} className="btoTematicas">
                <Row className="justify-content-center">
                        {Tematicas.map(tematica => (
                    <Col sm={12} md={8} className="bto" key={tematica.Url}>
                        <Link to={`./tematicas/${tematica.Url}` }>
                            <Row>
                                <Col xs={2} className="ico">
                                    <img src={tematica.Icon.url} alt={tematica.Tematica} />
                                </Col>
                                <Col className="title" >{tematica.Tematica}</Col>
                            </Row>
                        </Link>
                    </Col>
                    ))}
                </Row>
            </Col>
            <Col sm={6} className="bannerTematicas d-none d-md-block d-xxl-block">
                <img src="https://res.cloudinary.com/dtiqybyoh/image/upload/v1627069663/home_Dps_3683c159c6.png" alt="dps hogares" />
            </Col>
        </Row>

    )
}


