import React, {useState, useEffect, lazy, Suspense} from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { Board } from './content/Board'

const Polyfill = lazy(()=> import("./content/Store"));
const todos = fetchData('./content/Store')

export const Content = ({drawerWidth, params}) => {

    const useStyles = makeStyles((theme) => ({

        // necessary for content to be below app bar
        toolbar: theme.mixins.toolbar,
        drawerPaper: {
          width: drawerWidth
        },

        content: {
          flexGrow: 1,
          padding: theme.spacing(3),
        },
    }));
    const classes = useStyles();

    const [module, setModule] = useState(null);

   useEffect(() => {
      //const polyfill =  require("./content/Store");
      //polyfill = lazy(()=> import("./content/Store"));
      //setModule(polyfill);
   }, [params.layout]);

    return (
        <main className={classes.content}>
            <div className={classes.toolbar} />
            {
              (params.layout === 'board' || params.layout === undefined)
              &&<Board params={params}/>
            }
            {
              (params.layout != 'board' || params.layout != undefined)
              &&<Suspense fallback={<Spinner />}>
                  <Todos data={todos} />
                </Suspense>
            }

        </main>
    )
}
