import React from 'react'
import {Container, Row, Col } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faExclamationCircle } from '@fortawesome/free-solid-svg-icons'
import { useParams } from 'react-router'
import {Tematicas} from '../data/tematicas'
import {TopTematica} from '../components/navs/TopTematica'
import {IntroTematica} from '../components/tematicas/IntroTematica'
import {Acordeon} from '../components/tematicas/Acordeon'
import {TarjetaGiro} from '../components/tematicas/TarjetaGiro'
import {Historieta} from '../components/tematicas/Historieta'
import Video from '../components/tematicas/Video'
import {General} from '../components/tematicas/General'


export const Tematica = () => {

    const params = useParams();
    const tematica = Tematicas.find( tematica => tematica.Url === params.tematica);

    return (
        <div className="tematica">
            <TopTematica params={tematica}/>
            <IntroTematica params={tematica}/>
            <Container className="content">
                {tematica.Componentes.map( comp => (
                    <Row key={comp.Titulo} id={comp.id}>
                        <Col key={comp.id}>
                            <h1 className="text-uppercase">{comp.Titulo}</h1>
                            <hr/>
                            { 
                              (comp.Importante !== null)
                              &&<h4><FontAwesomeIcon icon={faExclamationCircle} style={{color:"red"}}/>  {comp.Importante}</h4>
                            }
                            <div className="contentComponet">
                            {
                                (comp.__typename === 'ComponentComponentesAcordeon')
                                && <Acordeon params={comp}/>
                            }
                            {
                                (comp.__typename === 'ComponentComponentesTarjetaGiro')
                                && <TarjetaGiro params={comp}/>
                            }
                            {
                                (comp.__typename === 'ComponentComponentesHistorieta')
                                && <Historieta params={comp}/>
                            }
                            {
                                (comp.__typename === 'ComponentComponentesVideo')
                                && <Video params={comp}/>
                            }
                            {
                                (comp.__typename === 'ComponentComponentesGeneral')
                                && <General params={comp}/>
                            }
                            </div>
                        </Col>
                    </Row>
                ))}
            </Container>
        </div>
    )
}
