import React from 'react';
import PropTypes from 'prop-types';
import { useParams } from 'react-router'
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import { makeStyles } from '@material-ui/core/styles';
import Menu from './component_admin/Menu';
import TopToolBar from './component_admin/TopToolBar';
import {Content} from './component_admin/Content';
import {Coownership} from '../data/Coownership'
import {Locals} from '../data/Locals'

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({

  root: {
    display: 'flex',
  },

  appBar: {
    backgroundColor:'#004c6f',
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth
    },
  },

}));

function Admin(props) {

  let params = useParams();
  if(params.idCoownership === undefined){
    params.locals = Locals.es; //Cargamos el listado de idiomas
    params.idCoownership = null; //Si no se tiene seleccionado una coopropiedad se colocal null para ser mostrado el tablero general
  }

  const classes = useStyles();
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const handleDrawerToggle = () => { setMobileOpen(!mobileOpen);}

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <TopToolBar handleDrawerToggle={handleDrawerToggle.bind(this)} params={params} />
      </AppBar>
      <Menu mobileOpen={mobileOpen} handleDrawerToggle={handleDrawerToggle.bind(this)} params={params} drawerWidth={drawerWidth} />
      <Content drawerWidth={drawerWidth} params={params} />
    </div>
  );
}

Admin.propTypes = {
  window: PropTypes.func,
};

export default Admin;
