import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import { makeStyles } from '@material-ui/core/styles';
import Menu from './component_admin/Menu';
import HideToolBar from './component_admin/HideToolBar';
import {Content} from './component_admin/Content';

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({

  root: {
    display: 'flex',
  },

  appBar: {
    backgroundColor:'#004c6f',
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth
    },
  },

}));

function Admin(props) {
  const classes = useStyles();
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const handleDrawerToggle = () => { setMobileOpen(!mobileOpen);}

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <HideToolBar handleDrawerToggle={handleDrawerToggle.bind(this)} />
      </AppBar>
      <Menu params={params, mobileOpen, handleDrawerToggle.bind(this)} drawerWidth={drawerWidth} />
      <Content drawerWidth={drawerWidth} params={params} />
    </div>
  );
}

Admin.propTypes = {
  window: PropTypes.func,
};

export default Admin;
