import React from 'react'
import { LoginScreen } from '../components/login/LoginScreen';
import {BrowserRouter as Router,  Switch, Route  } from "react-router-dom";
import { Internal } from './Internal';
import { useMediaQuery } from 'react-responsive';

export const Main = () => {

    let screen = ( useMediaQuery({ minWidth: 1824 }) )? {size:"isBigDesktop"}:"";
    screen = ( useMediaQuery({ minWidth: 1224 }) && screen === "")? {size:"isDesktop"}:screen;
    screen = ( useMediaQuery({ minWidth: 992 }) && screen === "")? {size:"isSmallDesktop"}:screen;
    screen = ( useMediaQuery({ minWidth: 768, maxWidth: 991 }) && screen === "")? {size:"isTablet"}:screen;
    screen = ( useMediaQuery({ maxWidth: 767 }) && screen === "")? {size:"isMobile"}:screen;
    screen = ( useMediaQuery({ minWidth: 768 }) && screen === "")? {size:"isDafault"}:screen; 

    return (
        <Router>
            <div>
                <Switch>
                    <Route exact path="/login" component={ LoginScreen }/>
                    <Route path="/" render={props => <Internal params={screen} />} />
                </Switch>
            </div>
        </Router>
    )
}
