import React from 'react'
import { Accordion, Card, Row, Col, Container } from "react-bootstrap";

export const TarjetaGiro = ({params}) => {
    params.Tarjeta.map(tarjeta => {
        console.log(tarjeta)
    })

    return (
        <Row>
            {   
                params.Tarjeta.map(tarjeta => (
                    <Col xs={12}>
                        <Card key={tarjeta.id} className="card-flip h-100">
                            <div className="card-front text-white bg-dark">
                                <div className={tarjeta.ColorA+" card-body"} >
                                {
                                    (tarjeta.ImagenA !== null)
                                    && <img src={tarjeta.ImagenA.url} style={{width:"100%"}} alt="" />
                                    || <div dangerouslySetInnerHTML={{ __html: tarjeta.CaraA }} /> 
                                }
                                </div>
                            </div>
                            <div className="card-back bg-white">
                                <div className={tarjeta.ColorB+" card-body cardRevers"}>
                                {
                                    (tarjeta.ImagenB !== null)
                                    && <img src={tarjeta.ImagenB.url} style={{width:"100%"}} alt="" />
                                    || <div dangerouslySetInnerHTML={{ __html: tarjeta.CaraB }} />
                                }
                                </div>
                            </div>
                        </Card>
                    </Col>
                ))
            }
        </Row>
    )
}


