import React, {useState} from 'react'
import {Audio} from './Audio'

export const General = ({params}) => {

    console.log(params);

    const [valAudioPlay, setAudioPlay] = useState(0);
    const handleAudio = (id, setValPlay, setIcon, sondRef, valPlay) => {
        setAudioPlay(id);
    }

    return (
        <div>
            {   
                (params.Audios !== null)
                && <Audio url={params.Audios.url} handleAudio={handleAudio} id={params.id} idPlay={valAudioPlay}/>
            }
            {   
                (params.Contenido !== null)
                && <div className="intro" dangerouslySetInnerHTML={{ __html: params.Contenido }} />
            }
            {   
                (params.Imagen !== null)
                && <img src={params.Imagen.url} alt={params.Titulo}/>
            }
        </div>
    )
}
