import React from 'react';
import YouTube from 'react-youtube';

class Video extends React.Component {

    constructor(props) {
        super(props);
    }

  render() {
    const opts = {
      playerVars: {
        autoplay: 1,
      },
    };

    return <YouTube className="video" videoId={this.props.params.VideoId} opts={opts} onReady={this._onReady} />;
  }

  _onReady(event) {
    event.target.pauseVideo();
  }
}

export default Video;
