import React, {useRef, useState} from 'react'
import {Row, Col, Container, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faStopCircle } from '@fortawesome/free-solid-svg-icons'

export const Historieta = ({params}) => {

    const [valPlay, setValPlay] = useState(true);
    const [icon, setIcon] = useState(faVolumeUp);
    const sondRef = useRef();

    const actionAudio = (id) => {
       if(valPlay === true){
        setIcon(faStopCircle);
        sondRef.current.play();

       }else{
        setIcon(faVolumeUp);
        sondRef.current.pause();
       }
        setValPlay(!valPlay);
    }

    return (
        <Row>
            {
                params.Vinetas.map(vineta => (
                    <div key={vineta.id} className='historieta'>
                        {   
                            (vineta.Audio !== null)
                            && <Button variant="primary" onClick={() =>  actionAudio(vineta.id) }>
                                <FontAwesomeIcon icon={icon}/> Escucha con atenci√≥n
                                <audio src={vineta.Audio} />
                            </Button>
                        }
                        <img src={vineta.Imagen.url} alt={vineta.Titulo}/>
                    </div>
                ))
            }
        </Row>
    )
}
